// This node will now receive multiple sources and process them all
const sources = $input.all();
const results = [];

for (const item of sources) {
  const sourceUrl = item.json.source_url || item.json;

  try {
    // Fetch each RSS feed
    // This will be handled by HTTP Request node in batch
    results.push({
      json: {
        source_url: sourceUrl,
        status: 'queued'
      }
    });
  } catch (error) {
    results.push({
      json: {
        source_url: sourceUrl,
        status: 'error',
        error: error.message
      }
    });
  }
}

return results;

// Clean and structure RSS data
const items = $input.all();
const cleanedItems = [];

for (const item of items) {
  try {
    const data = item.json;

    // Extract and clean data
    const cleanItem = {
      title: data.title || data.item?.title || 'No Title',
      description: (data.description || data.content || data.summary || '').replace(/<[^>]*>/g, '').substring(0, 500),
      url: data.link || data.url || '',
      published_date: data.pubDate || data.isoDate || data.date || new Date().toISOString(),
      source_url: data.source_url || $('Split Sources to Array').first().json.source_url,
      raw_content: data.contentSnippet || data.description || ''
    };

    // Only add if we have title and URL
    if (cleanItem.title !== 'No Title' && cleanItem.url) {
      cleanedItems.push({ json: cleanItem });
    }
  } catch (error) {
    // Skip items that can't be processed
    continue;
  }
}

return cleanedItems.length > 0 ? cleanedItems : [{ json: { error: 'No valid items found' } }];

