// Parse AI poll response
let pollData;
try {
  const aiContent = pollResponse.message?.content || pollResponse.choices?.[0]?.message?.content || '{}';
  const jsonMatch = aiContent.match(/\{[\s\S]*\}/);

  if (jsonMatch) {
    pollData = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found in AI response');
  }
} catch (error) {
  // Fallback if AI response parsing fails
  pollData = {
    question: `What is your opinion on: ${categoryData.title}?`,
    options: ['Very Positive', 'Positive', 'Negative', 'Very Negative'],
    context: categoryData.description?.substring(0, 150) || 'No context available'
  };
}

// Ensure exactly 4 options
if (!Array.isArray(pollData.options) || pollData.options.length !== 4) {
  pollData.options = ['Option A', 'Option B', 'Option C', 'Option D'];
}

// Return ONLY the fields needed for Supabase (no OpenAI metadata)
return {
  json: {
    // Original RSS data
    title: categoryData.title || 'Untitled News',
    description: categoryData.description || '',
    url: categoryData.url || '',
    published_date: categoryData.published_date || new Date().toISOString(),
    source_url: categoryData.source_url || '',

