// Aggregate all polls and posts created in this execution
const socialPosts = $('Store Social Posts').all();
const pendingPolls = $('Get Pending Polls').all();

// Group by poll
const pollSummary = {};

for (const post of socialPosts) {
  const data = post.json;
  const pollId = data.poll_id;

  if (!pollSummary[pollId]) {
    pollSummary[pollId] = {
      poll_id: pollId,
      platforms: [],
      post_count: 0
    };
  }

  pollSummary[pollId].platforms.push(data.platform);
  pollSummary[pollId].post_count++;
}

// Get poll details
const pollDetails = [];
for (const poll of pendingPolls) {
  const data = poll.json;
  const summary = pollSummary[data.id];

  pollDetails.push({
    id: data.id,
    title: data.title,
    category: data.category,
    question: data.poll_question,
    platforms: summary?.platforms || [],
    post_count: summary?.post_count || 0
  });
}

// Create summary statistics
const stats = {
  total_polls: pendingPolls.length,
  total_posts: socialPosts.length,
  by_category: {},
  by_platform: {}
};

// Count by category
for (const poll of pendingPolls) {
  const category = poll.json.category || 'Other';
  stats.by_category[category] = (stats.by_category[category] || 0) + 1;
}

// Count by platform
for (const post of socialPosts) {
  const platform = post.json.platform;
  stats.by_platform[platform] = (stats.by_platform[platform] || 0) + 1;
}

// Return single item with all aggregated data
return [{
  json: {
    polls: pollDetails,
    stats: stats,
    execution_time: new Date().toISOString()
  }
}];
